/*	LogMst.insert	*/

INSERT INTO LOG_MST(
	SEQ
	,PGM_ID
	,LOG_TIME
	,PLF_GBN
	,PLF_USER_ID
	,TITLE
	,SNAPSHOT
	,VIEW_CNT
	,CREATER
	,CREATE_DT
	,CREATE_IP
)SELECT
	(
		SELECT  IFNULL(MAX(SEQ),0)+1 AS SEQ
		FROM  LOG_MST
	)
	,#{PGM_ID}
	,#{LOG_TIME}
	,#{PLF_GBN}
	,#{PLF_USER_ID}
	,#{TITLE}
	,#{SNAPSHOT}
	,#{VIEW_CNT}
	,#{CREATER}
	,#{CREATE_DT}
	,#{CREATE_IP}
FROM DUAL
