mapper.insert:
/*	SysCodeLocale.insert	*/

INSERT INTO SYS_CODE_LOCALE(
	COMPANY
	, PRE_CD
	, LOCALE_CD
	, CODE
	, CODE_NM
	, REF1
	, REF2
	, REF3
	, REF4
	, REF5
	, MEMO
	, CREATER
	, CREATE_DT
	, CHANGER
	, CHANGE_DT
	, CHANGE_IP
)VALUES(
	#{COMPANY}
	,#{PRE_CD}
	,#{LOCALE_CD}
	,#{CODE}
	,#{CODE_NM}
	,#{REF1}
	,#{REF2}
	,#{REF3}
	,#{REF4}
	,#{REF5}
	,#{MEMO}
	,#{CREATER}
	,#{CREATE_DT}
	,#{CHANGER}
	,#{CHANGE_DT}
	,#{CHANGE_IP}
)

mapper.update:
/*	SysCodeLocale.update	*/
UPDATE  SYS_CODE_LOCALE
SET		CODE_NM		= #{CODE_NM}
		, REF1		= #{REF1}
		, REF2		= #{REF2}
		, REF3		= #{REF3}
		, REF4		= #{REF4}
		, REF5		= #{REF5}
		, MEMO		= #{MEMO}
		, CREATER	= #{CREATER}
		, CREATE_DT	= #{CREATE_DT}
		, CHANGER	= #{CHANGER}
		, CHANGE_DT	= #{CHANGE_DT}
		, CHANGE_IP	= #{CHANGE_IP}
WHERE	COMPANY		= #{COMPANY}
AND		PRE_CD		= #{PRE_CD}
AND		LOCALE_CD	= #{LOCALE_CD}
AND		CODE		= #{CODE}
