mapper.update:
/*	SysCode.update	*/
UPDATE  SYS_CODE
SET		CODE_NM		= #{CODE_NM}
		,REF1		= #{REF1}
		,REF2		= #{REF2}
		,REF3		= #{REF3}
		,REF4		= #{REF4}
		,REF5		= #{REF5}
		,SORT		= #{SORT}
		,USE_YN		= #{USE_YN}
		,MEMO		= #{MEMO}
		,CHANGER	= #{CHANGER}
		,CHANGE_DT	= SYSDATE()
		,CHANGE_IP	= #{CHANGE_IP}
WHERE	COMPANY	= #{COMPANY}
AND		PRE_CD	= #{PRE_CD}
AND		CODE	= #{CODE}

mapper.delete:
/*	SysCode.delete	*/
DELETE FROM SYS_CODE A
WHERE	A.COMPANY	= #{COMPANY}
AND		PRE_CD	= #{PRE_CD}
AND		CODE	= #{CODE}
