mapper.insert:
/*	SysCode.insert	*/

INSERT INTO SYS_CODE(
	COMPANY
	, PRE_CD
	, CODE
	, REF1
	, REF2
	, REF3
	, REF4
	, REF5
	, SORT
	, USE_YN
	, CREATER
	, CREATE_DT
	, CHANGER
	, CHANGE_DT
	, CHANGE_IP
)VALUES(
	#{COMPANY}
	,#{PRE_CD}
	,#{CODE}
	,#{REF1}
	,#{REF2}
	,#{REF3}
	,#{REF4}
	,#{REF5}
	,#{SORT}
	,#{USE_YN}
	,#{CREATER}
	,#{CREATE_DT}
	,#{CHANGER}
	,#{CHANGE_DT}
	,#{CHANGE_IP}
)

mapper.update:
/*	SysCode.update	*/
UPDATE  SYS_CODE
SET		REF1	= #{REF1}
		, REF2	= #{REF2}
		, REF3	= #{REF3}
		, REF4	= #{REF4}
		, REF5	= #{REF5}
		, SORT	= #{SORT}
		, USE_YN	= #{USE_YN}
		, CREATER	= #{CREATER}
		, CREATE_DT	= #{CREATE_DT}
		, CHANGER	= #{CHANGER}
		, CHANGE_DT	= #{CHANGE_DT}
		, CHANGE_IP	= #{CHANGE_IP}
WHERE	COMPANY	= #{COMPANY}
AND		PRE_CD	= #{PRE_CD}
AND		CODE	= #{CODE}
