	,#{REF2_CONFIG}
	,#{REF3_CONFIG}
	,#{REF4_CONFIG}
	,#{REF5_CONFIG}
	,#{USE_YN}
	,#{COM_YN}
	,#{MEMO}
	,#{CREATER}
	,#{CREATE_DT}
	,#{CHANGER}
	,#{CHANGE_DT}
