mapper.insert:
/*	SysCodeLocale.insert	*/

INSERT INTO SYS_CODE_LOCALE(
	COMPANY
	, PRE_CD
	, LOCALE_CD
	, CODE
	, CODE_NM
	, REF1
	, REF2
	, REF3
	, REF4
	, REF5
	, MEMO
	, CREATER
	, CREATE_DT
	, CHANGER
	, CHANGE_DT
	, CHANGE_IP
)VALUES(
	#{COMPANY}
	,#{PRE_CD}
	,#{LOCALE_CD}
	,#{CODE}
	,#{CODE_NM}
	,#{REF1}
	,#{REF2}
	,#{REF3}
	,#{REF4}
	,#{REF5}
	,#{MEMO}
	,#{CREATER}
	,#{CREATE_DT}
	,#{CHANGER}
	,#{CHANGE_DT}
	,#{CHANGE_IP}
)

mapper.update:
/*	SysCodeLocale.update	*/
UPDATE  SYS_CODE_LOCALE
SET		CODE_NM		= #{CODE_NM}
		, REF1		= #{REF1}
		, REF2		= #{REF2}
		, REF3		= #{REF3}
		, REF4		= #{REF4}
		, REF5		= #{REF5}
		, MEMO		= #{MEMO}
		, CREATER	= #{CREATER}
		, CREATE_DT	= #{CREATE_DT}
		, CHANGER	= #{CHANGER}
		, CHANGE_DT	= #{CHANGE_DT}
		, CHANGE_IP	= #{CHANGE_IP}
WHERE	COMPANY		= #{COMPANY}
AND		PRE_CD		= #{PRE_CD}
AND		LOCALE_CD	= #{LOCALE_CD}
AND		CODE		= #{CODE}

mapper.select:
/* SysCodeLocale.select */ SELECT A.COMPANY ,A.PRE_CD ,B.LOCALE_CD ,A.CODE ,B.CODE_NM ,A.REF1 ,A.REF2 ,A.REF4 ,A.REF5 ,B.MEMO ,B.CREATER ,B.CREATE_DT ,B.CHANGER ,B.CHANGE_DT ,B.CHANGE_IP FROM SYS_CODE A LEFT OUTER JOIN( SELECT A.COMPANY ,A.PRE_CD ,A.CODE ,A.LOCALE_CD ,A.CODE_NM ,A.REF1 ,A.REF2 ,A.REF4 ,A.REF5 ,A.MEMO ,A.CREATER ,A.CREATE_DT ,A.CHANGER ,A.CHANGE_DT ,A.CHANGE_IP FROM SYS_CODE_LOCALE A WHERE 1=1
AND A.PRE_CD = #{PRE_CD}
AND A.CODE!='0000000000'
) B ON(B.COMPANY= A.COMPANY AND B.PRE_CD = A.PRE_CD AND B.CODE = A.CODE) WHERE 1=1
AND A.PRE_CD = #{PRE_CD}
AND A.CODE!='0000000000'
