mapper.insert:
/*	SysFile.insert	*/
	INSERT INTO SYS_FILE(
		COMPANY
		,TABLE_NAME
		,SEQ
		,FILE_ID1
		,FILE_ID2
		,FILE_ID3
		,FILE_ID4
		,FILE_ID5
		,TEMP_FILE_ID
		,FILE_NAME
		,FILE_ORI_NAME
		,FILE_PATH
		,FILE_SIZE
		,CREATER
		,CREATE_DT
		,CHANGE_DT
		,CREATE_IP
	)SELECT
		 #{COMPANY}
		, #{TABLE_NAME}
		,(	SELECT  IFNULL(MAX(SEQ),0)+1
			FROM	 SYS_FILE
		)
		, #{FILE_ID1}
		, #{FILE_ID2}
		, #{FILE_ID3}
		, #{FILE_ID4}
		, #{FILE_ID5}
		, #{TEMP_FILE_ID}
		, #{FILE_NAME}
		, #{FILE_ORI_NAME}
		, #{FILE_PATH}
		, #{FILE_SIZE}
		, #{CREATER}
		, SYSDATE()
		, SYSDATE()
		, #{CREATE_IP}
	FROM DUAL

mapper.update:
/*	SysFile.updateTempFile	*/
UPDATE	SYS_FILE
SET		,COMPANY	= #{COMPANY}
		,TABLE_NAME	= #{TABLE_NAME}

		,FILE_ID1	= #{FILE_ID1}


		,FILE_ID2	= #{FILE_ID2}


		,FILE_ID3	= #{FILE_ID3}


		,FILE_ID4	= #{FILE_ID4}


		,FILE_ID5	= #{FILE_ID5}

		,TEMP_FILE_ID	= NULL
		,CHANGE_DT	= SYS_DATE()
WHERE	COMPANY		= #{COMPANY}
AND		TEMP_FILE_ID	= #{TEMP_FILE_ID}
AND		TABLE_NAME	= #{TABLE_NAME}

/*	SysFile.update	*/
UPDATE	SYS_FILE
SET		,COMPANY	= #{COMPANY}
		,SEQ		= #{SEQ}
		,TABLE_NAME	= #{TABLE_NAME}
		,FILE_ID1	= #{FILE_ID1}
		,FILE_ID2	= #{FILE_ID2}
		,FILE_ID3	= #{FILE_ID3}
		,FILE_ID4	= #{FILE_ID4}
		,FILE_ID5	= #{FILE_ID5}
		,TEMP_FILE_ID	= #{TEMP_FILE_ID}
		,FILE_NAME	= #{FILE_NAME}
		,FILE_ORI_NAME	= #{FILE_ORI_NAME}
		,FILE_PATH	= #{FILE_PATH}
		,FILE_SIZE	= #{FILE_SIZE}
		,CHANGE_DT	= SYS_DATE()
WHERE	COMPANY		= #{COMPANY}
AND		SEQ			= #{SEQ}
AND		TABLE_NAME	= #{TABLE_NAME}
AND		FILE_ID1	= #{FILE_ID1}

AND		FILE_ID2	= #{FILE_ID2}


AND		FILE_ID3	= #{FILE_ID3}


AND		FILE_ID4	= #{FILE_ID4}


AND		FILE_ID5	= #{FILE_ID5}


AND		TEMP_FILE_ID	= #{TEMP_FILE_ID}

mapper.select:
/*	SysFile.selectList	*/
	SELECT	A.COMPANY
			,A.SEQ
			,A.TABLE_NAME
			,A.FILE_ID1
			,A.FILE_ID2
			,A.FILE_ID3
			,A.FILE_ID4
			,A.FILE_ID5
			,A.TEMP_FILE_ID
			,A.FILE_NAME
			,A.FILE_ORI_NAME
			,A.FILE_PATH
			,A.FILE_SIZE
			,A.CREATER
			,DATE_FORMAT(A.CREATE_DT, '%Y%m%d%H%i%s') AS CREATE_DT
			,A.CREATE_IP
	FROM	SYS_FILE A	
	WHERE	COMPANY		= #{COMPANY}
	
	AND		SEQ			= #{SEQ}
	
	
	AND		TABLE_NAME	= #{TABLE_NAME}
	
	
	AND		FILE_ID1	= #{FILE_ID1}
	
	
	AND		FILE_ID2	= #{FILE_ID2}
	
	
	AND		FILE_ID3	= #{FILE_ID3}
	
	
	AND		FILE_ID4	= #{FILE_ID4}
	
	
	AND		FILE_ID5	= #{FILE_ID5}
	
	
	AND		TEMP_FILE_ID	= #{TEMP_FILE_ID}

mapper.delete:
/*	SysFile.delete	*/
	DELETE	FROM	SYS_FILE
	WHERE	COMPANY		= #{COMPANY}
	
	AND		SEQ			= #{SEQ}
	
	
	AND		TEMP_FILE_ID	= #{TEMP_FILE_ID}
