mapper.update:
/*	SysMenu.update	*/
UPDATE  SYS_MENU
SET		PRE_MENU_ID	= #{PRE_MENU_ID}
		,MENU_LVL		= #{MENU_LVL}
		,CLASS_NM		= #{CLASS_NM}
		,SHORT_NM		= #{SHORT_NM}
		,WIDGET_NM		= #{WIDGET_NM}
		,MENU_AUTH		= #{MENU_AUTH}
		,SORT			= #{SORT}
		,USE_YN			= #{USE_YN}
		,MEMO			= #{MEMO}
		,CHANGER		= #{CHANGER}
		,CHANGE_DT		= #{CHANGE_DT}
		,CHANGE_IP		= #{CHANGE_IP}
WHERE	SYSTEM		= #{SYSTEM}
AND		MENU_ID		= #{MENU_ID}

mapper.delete:
/*	SysMenu.delete	*/
DELETE FROM SYS_MENU
WHERE	SYSTEM		= #{SYSTEM}
AND		MENU_ID		= #{MENU_ID}

mapper.select:
/* SysMenu.selectList */ SELECT A.SYSTEM ,A.MENU_ID ,A.PRE_MENU_ID ,B.MENU_NM ,A.MENU_LVL ,A.CLASS_NM ,A.WIDGET_NM ,A.SHORT_NM ,A.MENU_AUTH ,A.SORT ,A.USE_YN ,B.MEMO ,A.CREATER ,A.CREATE_DT ,A.CHANGER ,A.CHANGE_DT ,A.CHANGE_IP FROM SYS_MENU A LEFT OUTER JOIN( SELECT A.SYSTEM ,A.LOCALE_CD ,A.MENU_ID ,A.MENU_NM ,A.MEMO FROM SYS_MENU_LOCALE A WHERE A.LOCALE_CD IN( SELECT MAX(REF1) FROM SYS_CODE_LOCALE WHERE PRE_CD='COM_000002' AND CODE = 'DEFAULT' ) )B ON(B.SYSTEM = A.SYSTEM AND B.MENU_ID = A.MENU_ID) WHERE 1=1
AND A.SYSTEM = #{SYSTEM}
ORDER BY A.SORT

/* SysMenu.selectLocaleList */ SELECT A.SYSTEM ,B.LOCALE_CD ,A.MENU_ID ,A.PRE_MENU_ID ,B.MENU_NM ,A.MENU_LVL ,A.CLASS_NM ,A.WIDGET_NM ,A.MENU_AUTH ,A.SORT ,A.USE_YN ,B.MEMO ,A.CREATER ,A.CREATE_DT ,A.CHANGER ,A.CHANGE_DT ,A.CHANGE_IP FROM SYS_MENU A, SYS_MENU_LOCALE B WHERE A.SYSTEM = B.SYSTEM AND A.MENU_ID = B.MENU_ID
AND A.SYSTEM = #{SYSTEM}
ORDER BY A.SORT
