mapper.update:
/*	SysMenu.update	*/
UPDATE  SYS_MENU
SET		PRE_MENU_ID	= #{PRE_MENU_ID}
		,MENU_NM		= #{MENU_NM}
		,MENU_LVL		= #{MENU_LVL}
		,CLASS_NM		= #{CLASS_NM}
		,WIDGET_NM		= #{WIDGET_NM}
		,MENU_AUTH		= #{MENU_AUTH}
		,SORT			= #{SORT}
		,USE_YN			= #{USE_YN}
		,MEMO			= #{MEMO}
		,CHANGER		= #{CHANGER}
		,CHANGE_DT		= #{CHANGE_DT}
		,CHANGE_IP		= #{CHANGE_IP}
WHERE	SYSTEM		= #{SYSTEM}
AND		MENU_ID		= #{MENU_ID}

mapper.delete:
/*	SysMenu.delete	*/
DELETE FROM SYS_MENU
WHERE	SYSTEM		= #{SYSTEM}
AND		MENU_ID		= #{MENU_ID}
