mapper.delete:
/*	SysMenuCode.delete	*/
DELETE FROM SYS_MENU
WHERE	SYSTEM		= #{SYSTEM}
AND		MENU_ID		= #{MENU_ID}

mapper.select:
/*	SysMenuCode.selectList	*/
SELECT	A.SYSTEM
		,A.MENU_ID
		,A.PRE_CD
		,A.CREATER
		,A.CREATE_DT
		,A.CREATE_IP
FROM	SYS_MENU_CODE A
WHERE	A.SYSTEM	= #{SYSTEM}
AND		A.MENU_ID	= #{MENU_ID}
