mapper.update:
/*	SysMenuLocale.update	*/

UPDATE SYS_MENU_LOCALE
SET		MENU_NM		= #{MENU_NM}
		,MEMO		= #{MEMO}
		,CHANGER	= #{CHANGER}
		,CHANGE_DT	= #{CHANGE_DT}
		,CHANGE_IP	= #{CHANGE_IP}
WHERE	SYSTEM		= #{SYSTEM}
AND		LOCALE_CD	= #{LOCALE_CD}
AND		MENU_ID		= #{MENU_ID}

mapper.delete:
/*	SysMenuLocale.delete	*/
DELETE	FROM	SYS_MENU
WHERE	MENU_ID		= #{MENU_ID}
