mapper.update:
/*	SysPropPage.update	*/
UPDATE  SYS_PROP_PAGE
SET		DOM_QRY	=#{DOM_QRY}
		,COMP_XTYPE	=#{COMP_XTYPE}
		,PROP_TYPE	=#{PROP_TYPE}
		,PROP_VALUEE=#{PROP_VALUEE}
		,CHANGER	=#{CHANGER}
		,CHANGE_DT	=#{CHANGE_DT}
		,CHANGE_IP	=#{CHANGE_IP}
WHERE	SYSTEM		=#{SYSTEM}
AND		MENU_ID		=#{MENU_ID}
AND		LOCALE_CD	=#{LOCALE_CD}
AND		SEQ			=#{SEQ}

mapper.delete:
/*	SysPropPage.delete	*/
DELETE FROM SYS_PROP_PAGE
WHERE	SYSTEM		= #{SYSTEM}
AND		MENU_ID		= #{MENU_ID}
